using Oracle.ManagedDataAccess.Client;\n\n// Create a new connection\nOracleConnection con = new OracleConnection("Data Source=(DESCRIPTION=(ADDRESS_LIST=(ADDRESS=(PROTOCOL=TCP)(HOST=myhost)(PORT=1521)))(CONNECT_DATA=(SERVER=DEDICATED)(SERVICE_NAME=mydatabase)));User Id=myuser;Password=mypassword;");\n\n// Open the connection\ncon.Open();\n\n// Create a new command\nOracleCommand cmd = new OracleCommand("SELECT * FROM mytable", con);\n\n// Execute the command\nOracleDataReader reader = cmd.ExecuteReader();\n\n// Read the data\nwhile (reader.Read())\n{\n    Console.WriteLine(reader["column1"].ToString());\n}\n\n// Close the connection\ncon.Close();