using iTextSharp.text.pdf;\n\n// Load the PDF file\nPdfReader pdfReader = new PdfReader("path/to/your/pdf/file.pdf");\n\n// Get the form fields\nAcroFields formFields = pdfReader.AcroFields;\n\n// Iterate over the form fields and extract the user entered values\nforeach (string fieldName in formFields.Fields.Keys)\n{\n    string fieldValue = formFields.GetField(fieldName);\n    Console.WriteLine(fieldName + ": " + fieldValue);\n}\n\n// Close the PDF reader\npdfReader.Close();