[Test]\npublic void TestBaseController()\n{\n    // Arrange\n    var mockFooRepository = new Mock<IFooRepository>();\n    var foo = new Foo { Bar = "Baz" };\n    mockFooRepository.Setup(r => r.GetFoo()).Returns(foo);\n\n    var baseController = new BaseController(mockFooRepository.Object);\n\n    // Act\n    var viewData = baseController.ViewData;\n\n    // Assert\n    Assert.AreEqual(foo, viewData["foo"]);\n}