using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a WebRequest instance\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://example.com/protected-page");\n\n        // Create a NetworkCredential instance with your credentials\n        NetworkCredential credentials = new NetworkCredential("username", "password");\n\n        // Set the Credentials property of the WebRequest\n        request.Credentials = credentials;\n\n        // Set the authentication type to Basic\n        request.Headers["Authorization"] = "Basic " + Convert.ToBase64String(System.Text.Encoding.ASCII.GetBytes("username:password"));\n\n        // Send the request and get the response\n        HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n\n        // Check the status code\n        if (response.StatusCode == HttpStatusCode.OK)\n        {\n            Console.WriteLine("Access granted");\n        }\n        else\n        {\n            Console.WriteLine("Access denied");\n        }\n    }\n}