// Example code to demonstrate how to use the Windows Performance Analyzer (WPA)\n// to collect CPU usage data\nusing System;\nusing System.Diagnostics;\n\nclass CpuProfilingExample\n{\n    static void Main(string[] args)\n    {\n        // Start a new CPU usage trace using WPA\n        ProcessStartInfo psi = new ProcessStartInfo();\n        psi.FileName = "wpa.exe";\n        psi.Arguments = "/collect /cpu /output:cpu_trace.etl";\n        Process.Start(psi);\n    }\n}