string orderBy = "column1";\nstring direction = "ASC";\n\nstring query = @"\n    SELECT *\n    FROM your_table\n    ORDER BY \n        CASE WHEN @OrderBy = 'column1' AND @Direction = 'ASC' THEN column1 END ASC,\n        CASE WHEN @OrderBy = 'column1' AND @Direction = 'DESC' THEN column1 END DESC,\n        CASE WHEN @OrderBy = 'column2' AND @Direction = 'ASC' THEN column2 END ASC,\n        CASE WHEN @OrderBy = 'column2' AND @Direction = 'DESC' THEN column2 END DESC;\n";\n\nSqlCommand cmd = new SqlCommand(query, connection);\ncmd.Parameters.AddWithValue("@OrderBy", orderBy);\ncmd.Parameters.AddWithValue("@Direction", direction);