using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\npublic static List<string> RemoveSequences(List<string> input)\n{\n    var result = new List<string>();\n    string previous = null;\n\n    foreach (var item in input)\n    {\n        if (item != previous)\n        {\n            result.Add(item);\n            previous = item;\n        }\n    }\n\n    return result;\n}