using System;\nusing System.Data;\n\nclass Program\n{\n    static void Main()\n    {\n        DataSet dataSet = new DataSet();\n        DataTable dataTable = new DataTable("YourTableName");\n        dataTable.Columns.Add("YourDateColumn", typeof(DateTime));\n        dataTable.Rows.Add(new DateTime(2022, 1, 1, 12, 0, 0));\n        dataTable.Rows.Add(new DateTime(2022, 1, 2, 12, 0, 0));\n        dataSet.Tables.Add(dataTable);\n\n        DateTime yourDateTime = new DateTime(2022, 1, 1, 12, 0, 0);\n\n        DataRow[] rows = dataSet.Tables["YourTableName"].Select("YourDateColumn = #" + yourDateTime.ToString("yyyy-MM-dd HH:mm:ss") + "#");\n\n        foreach (DataRow row in rows)\n        {\n            Console.WriteLine(row["YourDateColumn"]);\n        }\n    }\n}