[Test]\npublic void TestCalculateFutureDate()\n{\n    // Arrange\n    var fixedDate = new DateTime(2022, 1, 1);\n    var mockDateTimeProvider = new MockDateTimeProvider(fixedDate);\n    var myClass = new MyClass(mockDateTimeProvider);\n\n    // Act\n    var result = myClass.CalculateFutureDate();\n\n    // Assert\n    Assert.AreEqual(fixedDate.AddDays(30), result);\n}\n\nprivate class MockDateTimeProvider : IDateTimeProvider\n{\n    private readonly DateTime _fixedDate;\n\n    public MockDateTimeProvider(DateTime fixedDate)\n    {\n        _fixedDate = fixedDate;\n    }\n\n    public DateTime Now => _fixedDate;\n}