// Generate a unique token for each request\nstring token = Guid.NewGuid().ToString();\n\n// Store the token in the session\nSession["RequestToken"] = token;\n\n// Include the token in the hyperlink\nHyperLink1.NavigateUrl = "SaveSessionItem.aspx?token=" + token;\n\n// On the SaveSessionItem page, check if the token is valid\nstring requestToken = Request.QueryString["token"];\nstring sessionToken = (string)Session["RequestToken"];\n\nif (requestToken != sessionToken)\n{\n    // Token is invalid, do not process the request\n    return;\n}\n\n// Token is valid, process the request\nSaveSessionItem();