using System;\nusing System.Windows.Forms;\n\npublic class WindowPlacement\n{\n    public static void SaveWindowPlacement(Form form)\n    {\n        Properties.Settings.Default.WindowPlacement = form.WindowState == FormWindowState.Normal\n            ? $"{form.Location.X},{form.Location.Y},{form.Size.Width},{form.Size.Height}"\n            : $"{form.RestoreBounds.X},{form.RestoreBounds.Y},{form.RestoreBounds.Width},{form.RestoreBounds.Height}";\n        Properties.Settings.Default.Save();\n    }\n\n    public static void RestoreWindowPlacement(Form form)\n    {\n        string[] placement = Properties.Settings.Default.WindowPlacement.Split(',');\n        if (placement.Length == 4)\n        {\n            form.Location = new Point(int.Parse(placement[0]), int.Parse(placement[1]));\n            form.Size = new Size(int.Parse(placement[2]), int.Parse(placement[3]));\n        }\n    }\n}