using iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.parser;\nusing OfficeOpenXml;\n\npublic void ExtractPdfDataToExcel(string pdfFilePath, string excelFilePath)\n{\n    // Read PDF file\n    using (var pdfReader = new PdfReader(pdfFilePath))\n    {\n        var pdfText = new StringBuilder();\n        for (int page = 1; page <= pdfReader.NumberOfPages; page++)\n        {\n            var pageText = PdfTextExtractor.GetTextFromPage(pdfReader, page);\n            pdfText.Append(pageText);\n        }\n\n        // Extract data from PDF text\n        var data = pdfText.ToString().Split('\n');\n\n        // Create Excel file\n        using (var package = new ExcelPackage(new FileInfo(excelFilePath)))\n        {\n            var worksheet = package.Workbook.Worksheets.Add("Sheet1");\n\n            // Write data to Excel file\n            for (int i = 0; i < data.Length; i++)\n            {\n                worksheet.Cells[i + 1, 1].Value = data[i].Trim();\n            }\n\n            package.Save();\n        }\n    }\n}