public class Employee\n{\n    public string Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class DepartmentEmployees\n{\n    public Dictionary<string, List<Employee>> Departments { get; set; }\n\n    public DepartmentEmployees()\n    {\n        Departments = new Dictionary<string, List<Employee>>();\n    }\n\n    public void AddEmployee(string departmentId, Employee employee)\n    {\n        if (!Departments.TryGetValue(departmentId, out List<Employee> employees))\n        {\n            employees = new List<Employee>();\n            Departments[departmentId] = employees;\n        }\n        employees.Add(employee);\n    }\n}