// Load the assembly\nvar assembly = Assembly.LoadFrom("path/to/your/assembly.dll");\n\n// Get the web references\nvar webReferences = assembly.GetReferencedAssemblies()\n    .Where(a => a.FullName.StartsWith("System.Web.Services", StringComparison.OrdinalIgnoreCase));\n\n// Print the web references\nforeach (var webReference in webReferences)\n{\n    Console.WriteLine(webReference.FullName);\n}