using System;\nusing Smartmontools;\n\nclass SmartInfo\n{\n    public static void Main(string[] args)\n    {\n        // Initialize the smartmontools library\n        Smartctl smartctl = new Smartctl();\n\n        // Get the S.M.A.R.T. data for the first disk\n        SmartData data = smartctl.GetSmartData("/dev/sda");\n\n        // Print the temperature\n        Console.WriteLine("Temperature: " + data.Temperature);\n    }\n}