using System;\nusing System.IO;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        // Set the input folder and output file\n        string inputFolder = @"C:\Path\To\Images";\n        string outputFile = @"C:\Path\To\Output.mp4";\n\n        // Set the frame rate and output resolution\n        int frameRate = 30;\n        int width = 1920;\n        int height = 1080;\n\n        // Create a new FFmpeg process\n        Process ffmpeg = new Process();\n        ffmpeg.StartInfo.FileName = @"C:\Path\To\ffmpeg.exe";\n        ffmpeg.StartInfo.Arguments = $"-framerate {frameRate} -i {inputFolder}\image_%03d.jpg -c:v libx264 -crf 18 -c:a aac -b:a 128k -s {width}x{height} {outputFile}";\n        ffmpeg.StartInfo.UseShellExecute = false;\n        ffmpeg.StartInfo.RedirectStandardOutput = true;\n        ffmpeg.Start();\n\n        // Wait for the process to finish\n        ffmpeg.WaitForExit();\n    }\n}