using Microsoft.Exchange.WebServices.Data;\n\n// Set up EWS credentials and service URL\nstring ewsUrl = "https://your-exchange-server.com/ews/exchange.asmx";\nstring username = "your-exchange-username";\nstring password = "your-exchange-password";\n\n// Create an EWS service object\nExchangeService service = new ExchangeService(ExchangeVersion.Exchange2013_SP1);\nservice.Credentials = new NetworkCredential(username, password);\nservice.Url = new Uri(ewsUrl);\n\n// Retrieve the queued messages from IIS\n// ( implementation depends on your IIS queue storage )\n\n// Resubmit each message to Exchange using EWS\nforeach (var message in queuedMessages)\n{\n    EmailMessage email = new EmailMessage(service);\n    email.Subject = message.Subject;\n    email.Body = message.Body;\n    email.ToRecipients.Add(message.To);\n    email.Send();\n}