public class MyEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public bool IsLatest { get; set; }\n    public int VersionId { get; set; }\n    public ICollection<MyRelatedEntity> RelatedEntities { get; set; }\n}\n\npublic class MyRelatedEntity\n{\n    public int Id { get; set; }\n    public string Name { get; set; }\n    public int MyEntityId { get; set; }\n    public MyEntity MyEntity { get; set; }\n}\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyEntity> MyEntities { get; set; }\n    public DbSet<MyRelatedEntity> MyRelatedEntities { get; set; }\n\n    protected override void OnModelCreating(ModelBuilder modelBuilder)\n    {\n        modelBuilder.Entity<MyEntity>()\n            .HasMany(e => e.RelatedEntities)\n            .WithOne(e => e.MyEntity)\n            .HasForeignKey(e => e.MyEntityId)\n            .OnDelete(DeleteBehavior.Restrict);\n    }\n}