protected void Page_Load(object sender, EventArgs e)\n{\n    if (IsPostBack)\n    {\n        string panelStates = panelStates.Value;\n        string[] states = panelStates.Split(';');\n        foreach (string state in states)\n        {\n            string[] parts = state.Split('=');\n            string panelId = parts[0];\n            bool isExpanded = bool.Parse(parts[1]);\n            // Update the panel's state\n            CollapsiblePanelExtender cpe = (CollapsiblePanelExtender)FindControl(panelId);\n            cpe.Expanded = isExpanded;\n        }\n    }\n}