public class MyBizObject : IDataErrorInfo\n{\n    private bool _saving = false;\n\n    public void Save()\n    {\n        _saving = true;\n        // Save logic here\n    }\n\n    public string Error\n    {\n        get\n        {\n            if (!_saving) return string.Empty;\n            // Return actual error message here\n        }\n    }\n\n    public string this[string columnName]\n    {\n        get\n        {\n            if (!_saving) return string.Empty;\n            // Return actual error message for the column here\n        }\n    }\n}