public void InactiveCustomers(IEnumerable<Guid> customerIDs)\n{\n    // Create a DataTable to hold the IDs\n    DataTable tvp = new DataTable();\n    tvp.Columns.Add("CustomerID", typeof(Guid));\n\n    // Add the IDs to the DataTable\n    foreach (Guid id in customerIDs)\n    {\n        tvp.Rows.Add(id);\n    }\n\n    // Create the command and parameter\n    myAdoCommand.CommandText = "UPDATE Customer SET Active = 0 WHERE CustomerID in (SELECT CustomerID FROM @CustomerIDs)";\n    SqlParameter param = new SqlParameter("@CustomerIDs", SqlDbType.Structured);\n    param.TypeName = "dbo.CustomerIDTableType"; // You need to create this type in your DB\n    param.Value = tvp;\n\n    myAdoCommand.Parameters.Add(param);\n\n    // Execute the command\n    myAdoCommand.ExecuteNonQuery();\n}