[OperationContract]\n[WebGet(UriTemplate = "/GetExcelDocument/{id}")]\npublic Stream GetExcelDocument(int id)\n{\n    // Retrieve the Excel document from the database\n    byte[] documentBytes = GetDocumentBytesFromDatabase(id);\n\n    // Set the MIME type of the response\n    WebOperationContext.Current.OutgoingResponse.ContentType = "application/vnd.ms-excel";\n\n    // Return the document as a stream\n    return new MemoryStream(documentBytes);\n}