using System.Data.SqlClient;\n\n// Connect to Database A\nstring connectionStringA = "Server=myServerA;Database=myDatabaseA;...";\nusing (SqlConnection connectionA = new SqlConnection(connectionStringA))\n{\n    connectionA.Open();\n\n    // Connect to Database B\n    string connectionStringB = "Server=myServerB;Database=myDatabaseB;...";\n    using (SqlConnection connectionB = new SqlConnection(connectionStringB))\n    {\n        connectionB.Open();\n\n        // Copy data from ParentTable\n        SqlCommand commandA = new SqlCommand("SELECT * FROM ParentTable", connectionA);\n        SqlDataReader readerA = commandA.ExecuteReader();\n        while (readerA.Read())\n        {\n            SqlCommand commandB = new SqlCommand("INSERT INTO temp.ParentTable (ParentID, ...) VALUES (@ParentID, ...)", connectionB);\n            commandB.Parameters.AddWithValue("@ParentID", readerA["ParentID"]);\n            // ... add other parameters ...\n            commandB.ExecuteNonQuery();\n        }\n        readerA.Close();\n\n        // Copy data from ChildTable\n        commandA = new SqlCommand("SELECT * FROM ChildTable", connectionA);\n        readerA = commandA.ExecuteReader();\n        while (readerA.Read())\n        {\n            SqlCommand commandB = new SqlCommand("INSERT INTO temp.ChildTable (ChildID, ParentID, ...) VALUES (@ChildID, @ParentID, ...)", connectionB);\n            commandB.Parameters.AddWithValue("@ChildID", readerA["ChildID"]);\n            commandB.Parameters.AddWithValue("@ParentID", readerA["ParentID"]);\n            // ... add other parameters ...\n            commandB.ExecuteNonQuery();\n        }\n        readerA.Close();\n\n        // Copy data from GrandChildTable\n        commandA = new SqlCommand("SELECT * FROM GrandChildTable", connectionA);\n        readerA = commandA.ExecuteReader();\n        while (readerA.Read())\n        {\n            SqlCommand commandB = new SqlCommand("INSERT INTO temp.GrandChildTable (GrandChildID, ChildID, ...) VALUES (@GrandChildID, @ChildID, ...)", connectionB);\n            commandB.Parameters.AddWithValue("@GrandChildID", readerA["GrandChildID"]);\n            commandB.Parameters.AddWithValue("@ChildID", readerA["ChildID"]);\n            // ... add other parameters ...\n            commandB.ExecuteNonQuery();\n        }\n        readerA.Close();\n    }\n}