using Microsoft.SharePoint.Client;\n\nusing (var ctx = new ClientContext("https://your-sharepoint-site.sharepoint.com"))\n{\n    var web = ctx.Web;\n    var lists = web.Lists;\n\n    ctx.Load(lists);\n    await ctx.ExecuteQueryAsync();\n\n    foreach (var list in lists)\n    {\n        if (list.Title == "Your List Title")\n        {\n            list.Hidden = true;\n            list.Update();\n            await ctx.ExecuteQueryAsync();\n        }\n    }\n}