// In your controller\npublic class ContentController : Controller\n{\n    [HttpPost]\n    public ActionResult SaveContent(string content)\n    {\n        // Validate and sanitize user input\n        var sanitizedContent = HtmlSanitizer.Sanitize(content);\n\n        // Store content in database\n        var dbContext = new MyDbContext();\n        dbContext.Contents.Add(new Content { Html = sanitizedContent });\n        dbContext.SaveChanges();\n\n        return RedirectToAction("Index");\n    }\n}\n\n// In your view\n@model MyProject.Models.Content\n\n<textarea id="content-editor">\n    @Model.Html\n</textarea>\n\n<script>\n    tinymce.init({\n        selector: '#content-editor',\n        plugins: 'advlist autolink lists link image charmap print preview',\n        toolbar: 'undo redo | styleselect | bold italic | alignleft aligncenter alignright alignjustify | bullist numlist outdent indent | link image'\n    });\n</script>