// TemplatedUserControl.cs\npublic partial class TemplatedUserControl : UserControl\n{\n    public TemplatedUserControl()\n    {\n        InitializeComponent();\n    }\n\n    public Control Content { get; set; }\n\n    private void TemplatedUserControl_Load(object sender, EventArgs e)\n    {\n        if (Content != null)\n        {\n            Content.Dock = DockStyle.Fill;\n            this.Controls.Add(Content);\n        }\n    }\n}