using System;\nusing System.Threading;\n\nclass Program\n{\n    static void Main()\n    {\n        int altitude = 9876;\n        string direction = "NE";\n        int windSpeed = 5;\n\n        while (true)\n        {\n            Console.Write($"\rAltitude: {altitude}ft      Direction: {direction}");\n            Console.WriteLine();\n            Console.Write($"\rWind:     {windSpeed}km, {direction}");\n            Console.WriteLine();\n\n            // Move cursor to the beginning of the first line\n            Console.Write("\x1B[2A");\n\n            // Update values\n            altitude++;\n            windSpeed++;\n\n            Thread.Sleep(1000);\n        }\n    }\n}