using Microsoft.Win32;\n\npublic bool IsVisioInstalled(out string installPath)\n{\n    installPath = null;\n    using (RegistryKey key = Registry.LocalMachine.OpenSubKey(@"SOFTWARE\Microsoft\Visio"))\n    {\n        if (key != null)\n        {\n            object installRoot = key.GetValue("InstallRoot");\n            if (installRoot != null)\n            {\n                installPath = installRoot.ToString();\n                return true;\n            }\n        }\n    }\n    return false;\n}