[ServiceContract]\npublic interface IMyService\n{\n    [OperationContract]\n    void MyMethod();\n}\n\npublic class MyProxyService : IMyService\n{\n    private readonly ChannelFactory<IMyService> _factory;\n\n    public MyProxyService(string originalServiceUrl)\n    {\n        _factory = new ChannelFactory<IMyService>(new BasicHttpBinding(), originalServiceUrl);\n    }\n\n    public void MyMethod()\n    {\n        var channel = _factory.CreateChannel();\n        channel.MyMethod();\n        ((IClientChannel)channel).Close();\n    }\n}