using System.Diagnostics;\n\n// Get the HTML output of your .aspx page\nstring html = GetHtmlOutputFromPage(); // implement this method using Server.Execute\n\n// Create a PDF file\nstring pdfFile = "invoice.pdf";\nstring wkhtmltopdfPath = @"C:\Path\To\wkhtmltopdf.exe";\n\nusing (var process = new Process())\n{\n    process.StartInfo.FileName = wkhtmltopdfPath;\n    process.StartInfo.Arguments = $"--load-error-handling ignore --load-media-error-handling ignore --enable-local-file-access --page-size A4 --margin-top 0 --margin-bottom 0 --margin-left 0 --margin-right 0 {html} {pdfFile}";\n    process.Start();\n    process.WaitForExit();\n}