var request = (HttpWebRequest)WebRequest.Create("https://offsite-page.com");\nrequest.Method = "POST";\nrequest.ContentType = "application/x-www-form-urlencoded";\n\nvar fields = new Dictionary<string, string> { { "field1", field1Value }, { "field2", field2Value } };\nvar postData = string.Join("&", fields.Select(x => $"{x.Key}={x.Value}"));\n\nvar bytes = Encoding.UTF8.GetBytes(postData);\nrequest.ContentLength = bytes.Length;\n\nvar stream = request.GetRequestStream();\nstream.Write(bytes, 0, bytes.Length);\nstream.Close();\n\nvar response = (HttpWebResponse)request.GetResponse();\nResponse.Redirect(response.ResponseUri.ToString());