public class MaintenanceModeFilter : IActionFilter\n{\n    public void OnActionExecuting(ActionExecutingContext filterContext)\n    {\n        // Check if maintenance mode is enabled\n        if (IsMaintenanceModeEnabled())\n        {\n            // Redirect to maintenance page\n            filterContext.Result = new RedirectToRouteResult(\n                new RouteValueDictionary { { "controller", "Maintenance" }, { "action", "Index" } }\n            );\n        }\n    }\n\n    private bool IsMaintenanceModeEnabled()\n    {\n        // Query your database to check the maintenance mode indicator\n        using (var db = new YourDbContext())\n        {\n            return db.MaintenanceMode.SingleOrDefault().IsMaintenanceMode;\n        }\n    }\n}