using System;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\npublic class ModalForm : Form\n{\n    [DllImport("user32.dll")]\n    private static extern bool SetWindowPos(IntPtr hWnd, int hWndInsertAfter, int x, int y, int cx, int cy, int wFlags);\n\n    private const int HWND_TOPMOST = -1;\n    private const int HWND_NOTOPMOST = -2;\n\n    public ModalForm()\n    {\n        this.TopMost = true; // keep on top of other forms within the application\n        SetWindowPos(this.Handle, HWND_TOPMOST, 0, 0, 0, 0, 0); // make system-wide topmost\n    }\n\n    protected override void OnClosed(EventArgs e)\n    {\n        SetWindowPos(this.Handle, HWND_NOTOPMOST, 0, 0, 0, 0, 0); // reset topmost state\n        base.OnClosed(e);\n    }\n}