using System;\nusing System.IO;\nusing iTextSharp.text.pdf;\nusing iTextSharp.text.pdf.xfa;\n\npublic class XfaExample\n{\n    public static void Main(string[] args)\n    {\n        string src = "path_to_your_pdf.pdf";\n        string dest = "path_to_your_output_pdf.pdf";\n\n        using (var pdfReader = new PdfReader(src))\n        {\n            using (var pdfWriter = PdfWriter.GetInstance(pdfReader, new FileStream(dest, FileMode.Create)))\n            {\n                var xfaForm = new XfaForm(pdfReader);\n                var xfaFields = xfaForm.GetFields();\n\n                // populate fields\n                xfaFields.SetField("your_field_name", "your_field_value");\n\n                // write updated XFA form back to PDF\n                pdfWriter.AddDeveloperExtension(PdfWriter.PDFA_EXTENSION);\n\n                var xfaStream = new MemoryStream();\n                xfaForm.WriteTo(xfaStream);\n                pdfWriter.AddXfaForm(xfaStream.ToArray());\n            }\n        }\n    }\n}