using System.Configuration;\n\npublic class MySharedConfig\n{\n    private static readonly Configuration _sharedConfig;\n\n    static MySharedConfig()\n    {\n        _sharedConfig = ConfigurationManager.OpenMappedExeConfiguration(\n            new ExeConfigurationFileMap { ExeConfigFilename = "shared.config" },\n            ConfigurationUserLevel.None);\n    }\n\n    public static string GetSetting(string key)\n    {\n        return _sharedConfig.AppSettings.Settings[key].Value;\n    }\n}