using System;\nusing System.Linq;\n\npublic class ListComparator\n{\n    public static void CompareLists(string[] list1, string[] list2)\n    {\n        var commonElements = list1.Intersect(list2).ToArray();\n        var list1Indices = commonElements.Select(e => Array.IndexOf(list1, e)).ToArray();\n        var list2Indices = commonElements.Select(e => Array.IndexOf(list2, e)).ToArray();\n\n        for (int i = 0; i < commonElements.Length - 1; i++)\n        {\n            if (list1Indices[i] > list1Indices[i + 1])\n            {\n                Console.WriteLine($"Elements '{commonElements[i]}' and '{commonElements[i + 1]}' are in the wrong order in list 1.");\n            }\n        }\n    }\n\n    public static void Main()\n    {\n        string[] list1 = { "a", "c", "b", "d", "f", "e" };\n        string[] list2 = { "a", "d", "e", "f", "h" };\n\n        CompareLists(list1, list2);\n    }\n}