public ActionResult GetData(string filter, string sortColumn, string sortDirection, int pageNumber, int pageSize)\n{\n    // fetch data from database or other data source\n    var data = FetchData(filter, sortColumn, sortDirection, pageNumber, pageSize);\n\n    // create ViewModel instance\n    var viewModel = new PagedTableViewModel<MyData>\n    {\n        Data = data,\n        PageSize = pageSize,\n        PageNumber = pageNumber,\n        SortColumn = sortColumn,\n        SortDirection = sortDirection\n    };\n\n    // return JSON data\n    return Json(viewModel, JsonRequestBehavior.AllowGet);\n}