using HtmlAgilityPack;\n\npublic string CleanHtmlTags(string html)\n{\n    var doc = new HtmlDocument();\n    doc.LoadHtml(html);\n    return doc.DocumentNode.InnerText;\n}\n\n// Example usage:\nvar html = "<html xmlns=\"http://www.w3.org/1999/xhtml\" xml:lang=\"en\" lang=\"en\"><head><title>title</title></head><body><a href=\"aaa.asp?id=1\"> I want to get this text </a><div><h1>this is my want!!</h1><b>this is my want!!!</b></div></body></html>";\nvar cleanText = CleanHtmlTags(html);\nConsole.WriteLine(cleanText);