using System.Management;\n\nclass RemoteProcessKiller\n{\n    public static void KillProcess(string remoteMachineName, string processName)\n    {\n        // Create a new scope for the remote machine\n        ManagementScope scope = new ManagementScope(@"\\" + remoteMachineName + @"\root\cimv2");\n\n        // Create a new query to find the process\n        ObjectQuery query = new ObjectQuery("SELECT * FROM Win32_Process WHERE Name = '" + processName + "'");\n\n        // Create a new searcher to execute the query\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher(scope, query);\n\n        // Execute the query and get the results\n        ManagementObjectCollection results = searcher.Get();\n\n        // Iterate through the results and kill the process\n        foreach (ManagementObject result in results)\n        {\n            result.InvokeMethod("Terminate", null);\n        }\n    }\n}