using System.IO;\n\n// Create a new file system watcher\nvar watcher = new FileSystemWatcher(@"C:\Source");\n\n// Set the destination directory\nvar dest = @"D:\Destination";\n\n// Handle file changes\nwatcher.Changed += (sender, e) => {\n    // Sync the file\n    File.Copy(e.FullPath, Path.Combine(dest, e.Name), true);\n};\n\n// Start watching\nwatcher.EnableRaisingEvents = true;