protected void Page_Load(object sender, EventArgs e)\n{\n    string domainName = Request.ServerVariables["HTTP_HOST"];\n    string cssFile = GetCssFile(domainName);\n    Response.Write("<link rel='stylesheet' type='text/css' href='" + cssFile + "'>");\n}\n\nprivate string GetCssFile(string domainName)\n{\n    // logic to determine the CSS file based on the domain name\n    // for example:\n    if (domainName == "example1.com")\n    {\n        return "/skins/example1.css";\n    }\n    else if (domainName == "example2.com")\n    {\n        return "/skins/example2.css";\n    }\n    // ...\n}