// Client-side\nvar client = new MyServiceClient();\nclient.ClientCredentials.ServiceCertificate.Authentication.CertificateValidationMode = X509CertificateValidationMode.PeerOrChainTrust;\nclient.ClientCredentials.ServiceCertificate.DefaultCertificate = new X509Certificate2("path/to/server/certificate");\n\n// Service-side\nvar host = new ServiceHost(typeof(MyService));\nvar binding = new WsHttpBinding(SecurityMode.Transport);\nbinding.Security.Transport.ClientCredentialType = HttpClientCredentialType.Certificate;\nhost.AddServiceEndpoint(typeof(IMyService), binding, "https://myservice.com");