public class IndexedFilter\n{\n    private Dictionary<string, List<string>> index;\n\n    public IndexedFilter()\n    {\n        index = new Dictionary<string, List<string>>();\n    }\n\n    public void Add(string key, string value)\n    {\n        if (!index.TryGetValue(key, out var list))\n        {\n            list = new List<string>();\n            index[key] = list;\n        }\n        list.Add(value);\n    }\n\n    public IEnumerable<string> Filter(string prefix)\n    {\n        if (index.TryGetValue(prefix, out var list))\n        {\n            return list;\n        }\n        return Enumerable.Empty<string>();\n    }\n}