// Get the current column order\nvar columnOrder = dataGridView1.Columns.Cast<DataGridViewColumn>()\n    .Select(c => c.Name)\n    .ToList();\n\n// Reset the DataSource\ndataGridView1.DataSource = null;\ndataGridView1.DataSource = aDataSet.aTableName;\n\n// Reorder the columns\nforeach (var columnName in columnOrder)\n{\n    var column = dataGridView1.Columns[columnName];\n    if (column != null)\n    {\n        column.DisplayIndex = columnOrder.IndexOf(columnName);\n    }\n}