using MailKit.Net.Smtp;\nusing MailKit.Security;\nusing MimeKit;\n\n// Create a new message\nvar message = new MimeMessage();\nmessage.From.Add(new MailboxAddress("from@example.com"));\nmessage.To.Add(new MailboxAddress("to@example.com"));\nmessage.Subject = "Test message";\nmessage.Body = new TextPart("plain") { Text = "This is a test message." };\n\n// Send the message\nusing (var client = new SmtpClient())\n{\n    client.Connect("tempurl.org", 465, SecureSocketOptions.SslOnConnect);\n    client.Authenticate("username", "password");\n    client.Send(message);\n    client.Disconnect(true);\n}