using System.Diagnostics;\n\nstring serverName = "your-server-name";\nstring siteId = "your-iis-site-id";\n\nvar perf = new PerformanceCounter("ASP.NET Apps v2.0.50727", "Requests Total", "_LM_W3SVC_" + siteId + "_ROOT", serverName);\n\n// Get the initial value\nlong initialValue = perf.RawValue;\n\n// Wait for 1 second\nSystem.Threading.Thread.Sleep(1000);\n\n// Get the new value\nlong newValue = perf.RawValue;\n\nif (newValue > initialValue)\n{\n    Console.WriteLine("The IIS site is receiving requests.");\n}\nelse\n{\n    Console.WriteLine("The IIS site is not receiving requests.");\n}