using System;\nusing System.DirectoryServices;\nusing Microsoft.GroupPolicy;\n\nclass GroupPolicyConfigurator\n{\n    public static void Main()\n    {\n        // Define the group policy object\n        using (var gpo = new GPDomain("your_domain_name"))\n        {\n            // Define the policy settings\n            var controlPanelPolicy = gpo.GetGPOSetting("User Configuration", "Administrative Templates", "Control Panel");\n            var taskManagerPolicy = gpo.GetGPOSetting("User Configuration", "Administrative Templates", "System", "Ctrl+Alt+Del Options");\n\n            // Enable/Disable Control Panel\n            controlPanelPolicy.SetValue("Disable Control Panel", true); // or false to enable\n\n            // Enable/Disable Task Manager\n            taskManagerPolicy.SetValue("Disable Task Manager", true); // or false to enable\n\n            // Save the policy changes\n            gpo.Save();\n        }\n    }\n}