using System.IO;\nusing System.Web;\n\npublic class ImageManager\n{\n    private const string ImageFolder = "~/Images/Products/";\n\n    public void SaveImage(int productId, string imageType, HttpPostedFile file)\n    {\n        var folderPath = Server.MapPath(ImageFolder + productId + "/" + imageType);\n        if (!Directory.Exists(folderPath))\n        {\n            Directory.CreateDirectory(folderPath);\n        }\n\n        var filePath = Path.Combine(folderPath, file.FileName);\n        file.SaveAs(filePath);\n    }\n\n    public string GetImageUrl(int productId, string imageType)\n    {\n        var folderPath = Server.MapPath(ImageFolder + productId + "/" + imageType);\n        var filePath = Path.Combine(folderPath, "image.jpg"); // assuming a default image name\n        if (File.Exists(filePath))\n        {\n            return VirtualPathUtility.ToAbsolute(ImageFolder + productId + "/" + imageType + "/image.jpg");\n        }\n        return null;\n    }\n}