protected void Page_Load(object sender, EventArgs e)\n{\n    string id = Request.QueryString["id"];\n\n    if (id == "allcss")\n    {\n        Response.ContentType = "text/css";\n        // Write CSS contents to response\n        Response.Write(File.ReadAllText("path/to/your/css/file.css"));\n    }\n    else if (id == "alljs")\n    {\n        Response.ContentType = "application/javascript";\n        // Write JS contents to response\n        Response.Write(File.ReadAllText("path/to/your/js/file.js"));\n    }\n\n    Response.End();\n}