using System.Net;\nusing System.IO;\nusing System.Text;\n\n// Client ID and client secret from Windows Live Developer Center\nstring clientId = "your_client_id";\nstring clientSecret = "your_client_secret";\n\n// Authenticate and get the access token\nstring accessToken = GetAccessToken(clientId, clientSecret);\n\n// Use the access token to retrieve the user's contacts\nstring contactsUrl = "https://livecontacts.services.live.com/users/@L@{0}/rest/livecontacts/contacts";\nstring response = GetResponse(contactsUrl, accessToken);\n\n// Parse the response to extract the contact information\n// ...\n\nprivate string GetAccessToken(string clientId, string clientSecret)\n{\n    string authUrl = "https://login.live.com/oauth20_token.srf";\n    string postData = string.Format("grant_type=client_credentials&client_id={0}&client_secret={1}", clientId, clientSecret);\n\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(authUrl);\n    request.Method = "POST";\n    request.ContentType = "application/x-www-form-urlencoded";\n\n    Stream requestStream = request.GetRequestStream();\n    requestStream.Write(Encoding.UTF8.GetBytes(postData), 0, postData.Length);\n    requestStream.Close();\n\n    HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n    string accessToken = response.GetResponseHeader("Authorization");\n\n    return accessToken;\n}\n\nprivate string GetResponse(string url, string accessToken)\n{\n    HttpWebRequest request = (HttpWebRequest)WebRequest.Create(url);\n    request.Method = "GET";\n    request.Headers.Add("Authorization", "Bearer " + accessToken);\n\n    HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n    string responseContent = new StreamReader(response.GetResponseStream()).ReadToEnd();\n\n    return responseContent;\n}